mapper.select:
  	select 
	count(1)
  	from secondary_goods where user_id = #{sellId,jdbcType=BIGINT} and status = #{status,jdbcType=INTEGER}

  	select 
	count(1)
  	from secondary_order where user_id = #{userId,jdbcType=BIGINT}

  	select 
	count(1)
  	from secondary_order where user_id = #{userId,jdbcType=BIGINT} and order_state = #{orderStatus,jdbcType=INTEGER}

mapper.update:
update secondary_order
set order_state = 0
where  id = #{id,jdbcType=BIGINT} and user_id = #{userId,jdbcType=BIGINT}
