where order_id=#{orderId,jdbcType=VARCHAR} and order_state=1 and
user_id=#{userId,jdbcType=BIGINT}

update capital_user set remainder = remainder +
#{remainder,jdbcType=DECIMAL},gmt_modified=now() where
user_id=#{userId,jdbcType=BIGINT}
